import { Component ,OnInit} from '@angular/core';
import { DesignutilityService } from '../../../services/designutility.service';
import { from, map, toArray } from 'rxjs';

@Component({
  selector: 'app-pluck',
  templateUrl: './pluck.component.html',
  styleUrls: ['./pluck.component.css']
})
export class PluckComponent implements OnInit{

constructor(private service:DesignutilityService){

}
names:any[]=[];

  ngOnInit(): void {


    const users=[
      {name:"vedant",city:"Pune"},
      {name:"Abhishek",city:"Kolhapur"},
      {name:"Amit",city:"Sangli"},
      {name:"Prashant",city:"Ajara"}
    ]

  from(users).pipe(
    map(data=>data.name),
    toArray()

  ).subscribe(res=>{
    // console.log(res)
    this.names=res
  })

  }





}
<div class="container">
    <h2>Map</h2>
    <br>
    <hr>
    <div class="row">
        <div class="col-md-4">
            <strong>Ex -01</strong>
            <hr>
            <p class="bg-light border rounded py-2 px-3">
                <strong>Pluck :</strong>By Property
            </p>
            <table class="table">
                <tr>
                    <th>Name</th>
                </tr>
                <tr *ngFor="let x of names">
                    <td>{{x.name}}</td>
                </tr>
            </table>
        </div>
        <div class="col-md-4">
            <strong>Ex -02</strong>
            <hr>
            <p class="bg-light border rounded py-2 px-3">
                <strong>Pluck :</strong>data=> data * 3
            </p>

        </div>
        <div class="col-md-4">
            <strong>Ex -03</strong>
            <hr>
            <p class="bg-light border rounded py-2 px-3">
                <strong>Pluck :</strong>Object Data
            </p>
           <ul id="elcontainer">

           </ul>
        </div>
    </div>
</div>
