import { Component ,OnInit} from '@angular/core';
import { DesignutilityService } from '../../../services/designutility.service';
import { of } from 'rxjs';
@Component({
  selector: 'app-of-from',
  templateUrl: './of-from.component.html',
  styleUrls: ['./of-from.component.css']
})
export class OfFromComponent implements  OnInit{


  constructor(private service:DesignutilityService){

  }
  obsmsg:any;
  ngOnInit(): void {

    const obs1=of("vedant","aniket","krushna");

    obs1.subscribe(res=>{
      console.log(res);
      this.service.print(res,"elcontainer")
    })



    const obs2=of({"a":"vedant","b":"aniket","c":"krushna"})

    obs2.subscribe(res=>{
      console.log(res);
      this.obsmsg=res
    })
  }






}
<div class="container">
    <h2>Of & From</h2>
    <br>
    <strong>Ex - 01(of)</strong>
    <hr>
    <div class="row">
        <div class="col-md-6">
            <p class="bg-light py-2 px-3 border rounded ">

                <Strong>Data:</Strong>of('vedant','Aniket','Krushna')

            </p>
            <hr>
            <ul id="elcontainer"></ul>
        </div>

        <div class="col-md-6">
            <p class="bg-light py-2 px-3 border rounded ">

                <Strong>Data:</Strong>of(&#123;a:'vedant',b:'Aniket',c:'Krushna'&#125;)

            </p>
            <hr>
            <ul id="elcontainer2">
                <li> <strong>a: </strong>{{obsmsg.a}}</li>
                <li> <strong>b: </strong>{{obsmsg.b}}</li>
                <li> <strong>c: </strong>{{obsmsg.c}}</li>
            </ul>
        </div>
    </div>

    <h2>Of & From</h2>
    <br>
    <strong>Ex - 01(of)</strong>
    <hr>
    <div class="row">
        <strong>Array</strong>
        <div class="col-md-6">
            <p class="bg-light py-2 px-3 border rounded ">

                <Strong>Data:</Strong>of('vedant','Aniket','Krushna')

            </p>
            <hr>
            <ul id="elcontainer"></ul>
        </div>
    </div>
</div>
