import { Component, OnInit } from '@angular/core';
import { Observable, Subscription, interval, timeInterval } from 'rxjs';
import { DesignutilityService } from '../../../services/designutility.service';

@Component({
  selector: 'app-custom-observable',
  templateUrl: './custom-observable.component.html',
  styleUrls: ['./custom-observable.component.css']
})
export class CustomObservableComponent implements OnInit {


  constructor(private service: DesignutilityService) {

  }
  techstatus: any;
  techstatus2:any;
  broad!:Subscription;
  names:any;
  namestatus:any;
  state='Running';

  ngOnInit(): void {

    const source = Observable.create((observer: {
      complete(): unknown;
      error(arg0: any): unknown; next: (arg0: string) => void;
    }) => {

      setTimeout(() => {
        observer.next('Angular');
      }, 1000);

      setTimeout(() => {
        observer.next('TypeScript');
      }, 2000);

      setTimeout(() => {
        observer.next('JacaScript');
        observer.complete();

      }, 3000);

      setTimeout(() => {
        observer.next('Java');
        // observer.error(new Error('limit exceeded'));

      }, 4000);

      setTimeout(() => {
        observer.next('Django');


      }, 5000);

    })

    source.subscribe((res: any) => {
      // console.log(res);
      this.service.print(res, 'elcontainer')
    },

      (err: any) => {
        this.techstatus = 'error'
        this.state='Error'
      },
      () => {
        this.techstatus = 'completed';
        this.state='Success'
      }

    )



    const array=['Angular','TypeScript','JavaScript','Django','Python']
    const source2 = Observable.create((observer: any) => {
      let count=0
      setInterval(() => {
        observer.next(array[count]);

        if(count>=3){
          // this.broad.unsubscribe();
          observer.error(new Error('Limit Exceed'))

        }

        if(count>=5){
          observer.complete()

        }
        count++
      }, 1000)
    })


   this.broad=source2.subscribe((res: any)=>{

    // console.log(res);
    this.service.print(res,'elcontainer2');
    },
    ((err: any)=>{
      this.techstatus2='error'
    }),
    ()=>{
      this.techstatus2='completed'
    }
    )


    const namesArray=['Vedant','Nilesh','Jaydip','Achyut','Swarali','Poorva']
    const cusobs3=Observable.create((observer:any)=>{
     let count=0
      setInterval(()=>{
        observer.next(namesArray[count])
        if(count>=3){
          // observer.error(new Error('Error Occured'))
        }

        if(count>=5){
          observer.complete();
        }

        count++
      },1000)


    })


    cusobs3.subscribe((res:any)=>{

      console.log(res);
      this.names=res;
    },
    (err:any)=>{
      this.namestatus='error'
    },
    ()=>{
      this.namestatus='completed'
    }
    )
  }



}
<div class="container">
    <h2>Custom Observable</h2>
    <br>
    <hr>

    <div class="row">
        <div class="col-md-6">
            <strong>Ex -01(Manual)</strong>
            <hr>
            <ul [ngClass]="techstatus=='completed'? ' techcomplete' : techstatus=='error'? 'techerror' :'' " class="techlist " id="elcontainer">
                <!-- <li>Angular</li>
                <li>Typescript</li>
                <li>Java</li> -->
            </ul>
        </div>

        <div class="col-md-6">
            <strong>Ex -02(custom interval observal)</strong>
            <hr>
            <ul class="techlist" id="elcontainer2" [ngClass]="techstatus2=='completed'? 'techcomplete' :techstatus2=='error'? 'techerror' :'' ">
                <!-- <li>Angular</li>
                <li>Typescript</li>
                <li>Java</li> -->
            </ul>
        </div>
    </div>

    <div class="row">
        <div class="col-md-12">
            <strong>Ex -03(Random Names)</strong>
            <hr>
            <p class="bg-light border rounded py-2 px-3">
                <strong>Nmaes:</strong>('Vedant','Nilesh','Jaydip','Achyut','Swarali','Poorva')
            </p>
            <hr>
            <p>Curretn Name:<strong>{{names}}</strong></p>
            <span [ngClass]="namestatus=='completed'?'text-bg-success':namestatus=='error'? 'text-bg-danger' : 'text-bg-primary' "
            class="badge ">{{state}}</span>
        </div>
    </div>
</div>
