import { Component , OnInit} from '@angular/core';

@Component({
  selector: 'app-promice',
  templateUrl: './promice.component.html',
  styleUrls: ['./promice.component.css']
})
export class PromiceComponent implements OnInit{


result1:any;

  ngOnInit(): void {



  }

  promiseval:any;

  dellAvaialble(){
    return true
  }

  hpAvailable(){
    return false
  }

  myfun(){

    let p1=new Promise((resolve,reject)=>{

      // resolve("Promise is resolved");
      // reject("Promise is rejected");
      if(this.dellAvaialble()){
        setTimeout(() => {
          console.log("dell is purchased")
        }, 3000);
      }else if(this.hpAvailable()){
        setTimeout(() => {
          console.log("hp is available")
        }, 3000);

      }else{
        setTimeout(() => {
          console.log("Laptop is not available")
        }, 3000);
      }
    })

    p1.then(res=>{
      console.log("then code=>",res)
      this.promiseval=res;

    }).catch(res=>{
      console.log("catch code=>",res)
      this.promiseval=res;

    })
  }



  asyn(){

    let port=new Promise((resolve,reject)=>{

      setTimeout(() => {
        resolve("data fetched")
      }, 3000);

    })
    console.log(port)
  }

}
<div class="my-3">


    <h2>Promise</h2>

    <div class="md-4">
        <button class="btn btn-primary" (click)="myfun()">Click</button><br>

        <strong>{{ promiseval }}</strong>
    </div>


</div>
