import { Component , OnInit} from '@angular/core';

@Component({
  selector: 'app-promice',
  templateUrl: './promice.component.html',
  styleUrls: ['./promice.component.css']
})
export class PromiceComponent implements OnInit{


result1:any;

  ngOnInit(): void {



  }

  promiseval:any;

  dellAvaialble(){
    return false
  }

  hpAvailable(){
    return false
  }

  myfun(){

    let p1=new Promise((resolve,reject)=>{

      // resolve("Promise is resolved");
      // reject("Promise is rejected");
      if(this.dellAvaialble()){
        setTimeout(() => {
          resolve("dell is purchased")
        }, 3000);
      }else if(this.hpAvailable()){
        setTimeout(() => {
          resolve("hp is available")
        }, 3000);

      }else{
        setTimeout(() => {
          reject("Laptop is not available")
        }, 3000);
      }
    })

    p1.then(res=>{
      console.log("then code=>",res)
      this.promiseval=res;

    }).catch(res=>{
      console.log("catch code=>",res)
      this.promiseval=res;

    })
  }



  async async(){
    this.result1="fetching data"
    let port=new Promise((resolve,reject)=>{

      setTimeout(() => {
        resolve("data fetched")
      }, 3000);

    })

    let res= await port
    console.log(res)
    this.result1=res


  }



  result2:any;

  data={
    name:"Hp Laptop",
    harddisk:"2 TB",
    color:"black"
  }
  async1(){

    this.result2="fetching data"
    let buylaptop=new Promise((resolve,reject)=>{

      setTimeout(() => {
        resolve(this.data)
      }, 3000);

    })

    buylaptop.then(res=>{
      console.log(res);
      this.result1=res;
    })
  }
}
<div class="my-3">


    <h2>Promise</h2>

    <div class="md-4">
        <button class="btn btn-primary" (click)="myfun()">Click</button><br>
        <p>{{ promiseval }}</p>
    </div>

    <div class="md-4">
        <button class="btn btn-primary" (click)="async()">Click Me</button>
        <hr>
        <strong>{{ result1 }}</strong>
    </div>

    <div class="md-4">
        <button class="btn btn-primary" (click)="async1()">Buy Laptop</button>
        <hr>
        <strong>{{ result2 }}</strong>
    </div>
</div>
